// QuizData.js
const quizData = [
    {
      question: "What is the primary cause of forest habitat loss in Victoria?",
      options: [
        "Land clearing and logging",
        "Urban gardening"
      ],
      correctAnswer: "Land clearing and logging"
    },
    {
      question: "Which species is critically endangered due to forest loss in Victoria?",
      options: [
        "Leadbeater’s Possum",
        "Koala"
      ],
      correctAnswer: "Leadbeater’s Possum"
    },
    {
      question: "Which forest type is commonly found in the Central Highlands of Victoria?",
      options: [
        "Wet eucalypt forests",
        "Alpine forests"
      ],
      correctAnswer: "Wet eucalypt forests"
    },
    {
      question: "Which Victorian policy is specifically aimed at protecting biodiversity?",
      options: [
        "Flora and Fauna Guarantee Act 1988",
        "Road Safety Plan"
      ],
      correctAnswer: "Flora and Fauna Guarantee Act 1988"
    },
    {
      question: "Can individuals influence local forest policy by contacting their local council?",
      options: [
        "Yes",
        "No"
      ],
      correctAnswer: "Yes"
    },
    {
      question: "What is a major threat to Victorian forests besides logging?",
      options: [
        "Bushfires",
        "Drought-resistant plants"
      ],
      correctAnswer: "Bushfires"
    },
    {
      question: "Which policy provides action plans for threatened species?",
      options: [
        "Biodiversity 2037 Strategy",
        "Planning and Environment Act"
      ],
      correctAnswer: "Biodiversity 2037 Strategy"
    },
    {
      question: "Are rainforest habitats common in western Victoria?",
      options: [
        "No",
        "Yes"
      ],
      correctAnswer: "No"
    },
    {
      question: "What kind of logging is currently banned in Victoria’s native forests (as of 2024)?",
      options: [
        "Native forest logging",
        "Plantation logging"
      ],
      correctAnswer: "Native forest logging"
    },
    {
      question: "What’s the best way to check if a species is protected in your area?",
      options: [
        "Use the DELWP interactive map",
        "Ask your neighbor"
      ],
      correctAnswer: "Use the DELWP interactive map"
    },
    {
      question: "Do Victorian forest management policies include First Nations consultation?",
      options: [
        "Yes",
        "No"
      ],
      correctAnswer: "Yes"
    },
    {
      question: "What role do councils play in environmental conservation?",
      options: [
        "Policy enforcement and community programs",
        "Only property rate collection"
      ],
      correctAnswer: "Policy enforcement and community programs"
    },
    {
      question: "Which area is known for Dry Forest and Box-Ironbark woodlands?",
      options: [
        "Northern Victoria",
        "Eastern Victoria"
      ],
      correctAnswer: "Northern Victoria"
    },
    {
      question: "Can citizen science help in forest conservation?",
      options: [
        "Yes",
        "No"
      ],
      correctAnswer: "Yes"
    },
    {
      question: "Which Act focuses on land use planning in Victoria?",
      options: [
        "Planning and Environment Act 1987",
        "Forest Fire Management Act"
      ],
      correctAnswer: "Planning and Environment Act 1987"
    },
    {
      question: "What’s one sign of ecosystem degradation?",
      options: [
        "Habitat fragmentation",
        "Increased native biodiversity"
      ],
      correctAnswer: "Habitat fragmentation"
    },
    {
      question: "Is the Greater Glider found in Alpine forests?",
      options: [
        "No",
        "Yes"
      ],
      correctAnswer: "No"
    },
    {
      question: "What’s the benefit of writing to your council about forest issues?",
      options: [
        "Creates policy pressure and visibility",
        "Gets you a free tree"
      ],
      correctAnswer: "Creates policy pressure and visibility"
    },
    {
      question: "Who manages most of Victoria’s public forests?",
      options: [
        "DELWP (Department of Energy, Environment and Climate Action)",
        "Tourism Victoria"
      ],
      correctAnswer: "DELWP (Department of Energy, Environment and Climate Action)"
    },
    {
      question: "Can forest protection help reduce climate change?",
      options: [
        "Yes",
        "No"
      ],
      correctAnswer: "Yes"
    }
  ];

  export default quizData;
